mapper.select:
select a.*
from now_boss a
where a.group_id = #{groupId}
order by a.boss_rounds desc, a.boss_order
limit 5;

select a.*
from now_boss a
where a.group_id = #{groupId}

    and a.boss_order=#{bossOrder}

order by a.boss_rounds desc, a.boss_order
limit 1;

mapper.update:
update now_boss
set hp_now=hp_now + #{damage}
where now_id = #{nowBossId};
