        					if(localesT[index].dataset[i]){useable[i] = true;}
        				}
        			}
        			localesT_storage[index] = {
        				"cn" : dom.dataset["cn"] || "",
        				"tw" : dom.dataset["tw"] || "",
        				"jp" : dom.dataset["jp"] || "",
        				"kr" : dom.dataset["kr"] || "",
        				"en" : dom.dataset["en"] || "",
        				"th" : dom.dataset["th"] || ""
        			};
        		});
        	}
        	function Locales(){
        		for (var i of lang_list){
        			if(useable[i]){
        				LocalesControl(i);
        			}
        		}
        	}
        	function LocalesControl(i){
        		document.getElementById(i).classList.remove('disable')
        		document.getElementById(i).addEventListener("click", (e) => {
        			if(e.currentTarget != last_active){
        				setLocales(i);
        				last_active = e.currentTarget;
        			}
        		});
        	}
        	function setLocales(l){
        		lang = l;
        		document.getElementById(lang).classList.add('lang_active');
        		last_active.classList.remove('lang_active');
        		document.getElementById("mw-content-text").setAttribute("lang",lang_font[lang]);
        		document.getElementById("firstHeading").setAttribute("lang",lang_font[lang]);
        		locales.forEach(function(dom,index){
        			dom.innerHTML = locales_storage[index][lang];
        		});
        		localesB.forEach(function(dom,index){
        			dom.innerHTML = localesB_storage[index][lang];
        		});
        		localesT.forEach(function(dom,index){
        			dom.innerText = localesT_storage[index][lang];
        		});
        		localStorage.setItem("ba_lang",lang);
        	}
        	function clearLocales(){
        		locales.forEach(function(ele,index){
        			for (var i of lang_list){
        				ele.removeAttribute("data-"+i);
        			}
        		});
        		localesB.forEach(function(ele,index){
        			for (var i of lang_list){
        				ele.removeAttribute("data-"+i);
        			}
        		});
        		localesT.forEach(function(ele,index){
        			for (var i of lang_list){
        				ele.removeAttribute("data-"+i);
        			}
        		});
        	}
        	function initLocales(){
        		if(lang!='cn' && useable[lang]){
        			setLocales(lang);
        			last_active = document.getElementById(lang);
        		}
        	}
        	function b64DecodeUnicode(str){
        		return decodeURIComponent(atob(str).split('').map(function(c) {
        			return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        		}).join(''));
            }
        	getLocales();
        	Locales();
        	initLocales();
        	clearLocales();
        });
        })()
        							</script>
        							<script>
        								FFData={
        	"userValue":{
        		"iOM":["有","无"]
        	}
        }
        FFFun={
        	"initialize":function(){
        		FFMeta=[]
        		let FlourFilter=document.getElementsByClassName("FlourFilter")
        		for(let i=0,ilen=FlourFilter.length;i<ilen;i++){
        			FFMeta[i]={}
        			FFMeta[i].fliters=[]
        			let filter=FlourFilter[i].getElementsByClassName("filters")[0].getElementsByClassName("filter")
        			for(let j=0,jlen=filter.length;j<jlen;j++){
        				let group=filter[j].dataset.group
        				FFMeta[i].fliters[group]={}
        				FFMeta[i].fliters[group].mode=filter[j].dataset.mode
        				FFMeta[i].fliters[group].selected=new Set()
        				let pore=filter[j].getElementsByClassName("pore")
        				for(let k=0,klen=pore.length;k<klen;k++){
        					pore[k].onclick=function(){FFFun.select(i,group,pore[k].dataset.attr)}
        					if(pore[k].classList.contains("selected")){
        						let selected=FFMeta[i].fliters[group].selected
        						if(selected.has(pore[k].dataset.attr)){
        							selected.delete(pore[k].dataset.attr)
        						}
        						else{
        							if(FFMeta[i].fliters[group].mode=="me"){
        								selected.clear()
        							}
        							selected.add(pore[k].dataset.attr)
        						}
        					}
        				}
        				FFMeta[i].fliters[group].pore=pore
        				let poreCancel=filter[j].getElementsByClassName("poreCancel")
        				for(let k=0,klen=poreCancel.length;k<klen;k++){
        					poreCancel[k].onclick=function(){FFFun.remove(i,group)}
        				}
        			}
        			let fliterCancel=FlourFilter[i].getElementsByClassName("filters")[0].getElementsByClassName("fliterCancel")
        			for(let j=0,jlen=fliterCancel.length;j<jlen;j++){
        				fliterCancel[j].onclick=function(){FFFun.remove(i)}
        			}
        			FFMeta[i].flour=[]
        			let flour=FlourFilter[i].getElementsByClassName("flourpackage")[0].getElementsByClassName("flour")
        			let isNest=FlourFilter[i].dataset.hasOwnProperty("nest")
        			for(let j=0,jlen=flour.length;j<jlen;j++){
        				if(isNest){
        					if(FlourFilter[i].dataset.nest==flour[j].dataset.nest){
        						FFMeta[i].flour.push(flour[j])
        					}
        				}
        				else{
        					FFMeta[i].flour.push(flour[j])
        				}
        			}
        			for(let key in FFData.userValue){
        				FFFun.setUserValue(key,...FFData.userValue[key])
        			}
        			this.run(i)
        		}
        	},
        	"select":function(MI,FI,attr){
        		let selected=FFMeta[MI].fliters[FI].selected
        		if(selected.has(attr)){
        			selected.delete(attr)
        		}
        		else{
        			if(FFMeta[MI].fliters[FI].mode=="me"){
        				selected.clear()
        			}
        			selected.add(attr)
        		}
        		this.run(MI)
        	},
        	"remove":function(MI,FI){
        		let fliters=FFMeta[MI].fliters
        		if(!FI){
        			for(let i=0,ilen=fliters.length;i<ilen;i++){
        				if(!fliters[i]){
        					continue
        				}
        				fliters[i].selected.clear()
        			}
        		}
        		else{
        			fliters=FFMeta[MI].fliters[FI].selected.clear()
        		}
        		this.run(MI)
        	},
        	"run":function(MI){
        		let fliters=FFMeta[MI].fliters
        		for(let i=0,ilen=fliters.length;i<ilen;i++){
        			if(!fliters[i]){
        				continue
        			}
        			let selected=fliters[i].selected
        			let pore=fliters[i].pore
        			for(let j=0,jlen=pore.length;j<jlen;j++){
        				if(selected.has(pore[j].dataset.attr)){
        					pore[j].classList.add("selected")
        				}
        				else{
        					pore[j].classList.remove("selected")
        				}
        			}
        		}
        		let flour=FFMeta[MI].flour
        		let count=0
        		let total=flour.length
        		i:for(let i=0,ilen=flour.length;i<ilen;i++){
        			let attr=flour[i].dataset.attr.split("|")
        			for(let j=0,jlen=attr.length;j<jlen;j++){
        				if(j>=FFMeta[MI].fliters.length){
        					break
        				}
        				if(!FFMeta[MI].fliters[j]){
        					continue
        				}
        				let selected=FFMeta[MI].fliters[j].selected
        				if(selected.size==0){
        					continue
        				}
        				let sattr=attr[j].split(",")
        				if(selected.size==1){
        					if(!sattr.includes([...selected][0])){
        						flour[i].style.display="none"
        						continue i
        					}
        					continue
        				}
        				let mode=FFMeta[MI].fliters[j].mode
        				let display
        				if(mode=="or"){
        					display=false
        					for(let k=0,klen=sattr.length;k<klen;k++){
        						if(selected.has(sattr[k])){
        							display=true
        							break
        						}
        					}
        				}
        				else if(mode=="and"){
        					display=true
        					selected=[...selected]
        					for(let k=0,klen=selected.length;k<klen;k++){
        						if(!sattr.includes(selected[k])){
        							display=false
        							break
        						}
        					}
        				}
        				if(!display){
        					flour[i].style.display="none"
        					continue i
        				}
        			}
        			flour[i].style.display=""
        			count++
        		}
        		let FlourFilter=document.getElementsByClassName("FlourFilter")[MI]
        		let flourCount=FlourFilter.getElementsByClassName("flourCount")
        		let flourTotal=FlourFilter.getElementsByClassName("flourTotal")
        		let isNest=FlourFilter.dataset.hasOwnProperty("nest")
        		for(let i=0,ilen=flourCount.length;i<ilen;i++){
        			if(isNest){
        				if(FlourFilter.dataset.nest==flourCount[i].dataset.nest){
        					flourCount[i].innerHTML=count
        				}
        			}
        			else{
        				flourCount[i].innerHTML=count
        			}
        		}
        		for(let i=0,ilen=flourTotal.length;i<ilen;i++){
        			if(isNest){
        				if(FlourFilter.dataset.nest==flourTotal[i].dataset.nest){
        					flourTotal[i].innerHTML=total
        				}
        			}
        			else{
        				flourTotal[i].innerHTML=total
        			}
        		}
        	},
        	"setUserValue":function(key,T,F){
        		if(localStorage[key]){
        			let arr=localStorage[key].split(",")
        			let reg=RegExp(key+"\\((.*?)\\)")
        			for(let i=0;i<FFMeta.length;i++){
        				for(let j=0;j<FFMeta[i].flour.length;j++){
        					if(reg.test(FFMeta[i].flour[j].dataset.attr)){
        						if(arr.includes(FFMeta[i].flour[j].dataset.attr.match(reg)[1])){
        							FFMeta[i].flour[j].dataset.attr=FFMeta[i].flour[j].dataset.attr.replace(reg,T)
        						}
        						else{
        							FFMeta[i].flour[j].dataset.attr=FFMeta[i].flour[j].dataset.attr.replace(reg,F)
        						}
        					}
        				}
        			}
        		}
        	}
        }
        if(document.readyState=="complete"){
        	FFFun.initialize()
        }
        else{
        	window.addEventListener("load",function(){
        		FFFun.initialize()
        	})
        }
        							</script>
